mapper.select:
SELECT b.sno, b.boardtype, b.nal, b.subject, b.doc, b.hashtag, b.viewcount, b.thumbup, b.thumbdown, (b.thumbup-b.thumbdown) AS thumb, b.board_delete, m.id, m.nickname, m.profile_img, qb.qna_horsehead AS horsehead, qb.qna_pixel_reward, qb.qna_status, COUNT(r.repl_sno) AS countRepl FROM board b LEFT JOIN member m ON b.id = m.id LEFT JOIN repl r ON b.sno = r.sno LEFT JOIN qna_board qb ON b.sno = qb.sno WHERE m.nickname LIKE '%${findStr}%' OR b.hashtag LIKE '%${findStr}%' OR b.subject LIKE '%${findStr}%' OR b.doc LIKE '%${findStr}%' GROUP BY b.sno, b.boardtype, b.nal, b.subject, b.doc, b.hashtag, b.viewcount, b.thumbup, b.thumbdown, thumb, b.board_delete, m.id, m.nickname, m.profile_img, qb.qna_horsehead, qb.qna_pixel_reward, qb.qna_status HAVING b.boardtype = #{boardtype}
AND qb.qna_horsehead = #{horsehead}
ORDER BY b.nal DESC, b.sno DESC
ORDER BY thumb DESC, b.nal DESC, b.sno DESC
ORDER BY countRepl DESC, b.nal DESC, b.sno DESC
ORDER BY b.viewcount DESC, b.nal DESC, b.sno DESC
LIMIT ${startNo}, ${listSize}

SELECT b.sno, b.boardtype, b.nal, b.subject, b.doc, b.hashtag, b.viewcount, b.thumbup, b.thumbdown, (b.thumbup-b.thumbdown) AS thumb, b.board_delete, m.id, m.nickname, m.profile_img, ib.infoshare_horsehead AS horsehead, COUNT(r.repl_sno) AS countRepl FROM board b LEFT JOIN member m ON b.id = m.id LEFT JOIN repl r ON b.sno = r.sno LEFT JOIN infoshare_board ib ON b.sno = ib.sno WHERE m.nickname LIKE '%${findStr}%' OR b.hashtag LIKE '%${findStr}%' OR b.subject LIKE '%${findStr}%' OR b.doc LIKE '%${findStr}%' GROUP BY b.sno, b.boardtype, b.nal, b.subject, b.doc, b.hashtag, b.viewcount, b.thumbup, b.thumbdown, thumb, b.board_delete, m.id, m.nickname, m.profile_img, ib.infoshare_horsehead HAVING b.boardtype = #{boardtype}
AND ib.infoshare_horsehead = #{horsehead}
ORDER BY b.nal DESC, b.sno DESC
ORDER BY thumb DESC, b.nal DESC, b.sno DESC
ORDER BY countRepl DESC, b.nal DESC, b.sno DESC
ORDER BY b.viewcount DESC, b.nal DESC, b.sno DESC
LIMIT ${startNo}, ${listSize}

